 $name = "";
 $pricetype = "static";
 	$link = "#";
 
 if($fields['product']){
	 
	$product = get_fields($fields['product']);
	$name = $product['brand']." ".$product['model'];
	
	$metas = get_post_meta($fields['product']);
	
	$useful = array();

						foreach($metas as $key => $meta){
							if(strpos($key, "_cegg_data") !== false){		
								$unserialize = unserialize($meta[0]);
								$key2 = array_keys($unserialize)[0];
								$useful[] = $unserialize[$key2];	
							}
						}

						$oos = array(); $is = array(); $isunknownprice = array(); $finaldomain = NULL;

						usort($useful, fn($a, $b) => $a['price'] <=> $b['price']);
						
						

						if(count($useful) > 0){
		
						$useful2 = array();
						$useful3 = array();
						$useful4 = array();
							foreach($useful as $key => $item){
								if($item['price'] == 0 && $item['stock_status'] == 1) {
									$useful2[] = $item;
									unset($useful[$key]);
								} elseif($item['stock_status'] == -1){
									$useful3[] = $item;
									unset($useful[$key]);
								} elseif($item['stock_status'] == -1 && $item['price'] == 0) {
									$useful4[] = $item;
									unset($useful[$key]);
								}
							}

							$useful = array_merge($useful,$useful2);
							$useful = array_merge($useful,$useful3);
							$useful = array_merge($useful,$useful4);
							
						}
	
	//print_r($useful[0]);
	
	if($useful[0]['price'] > 0){
		$link = $useful[0]['url'];
		$pricetype = "dynamic";
	}
	
 }
 
 if($fields['product_name']){
	$name = $fields['product_name'];
 }
 
 if($link == "#"){
	if($useful[0]['domain'] == "amazon.com"){
		$link = $useful[0]['url'];
	}
 }
 
 if($link == "#" && $fields['affiliate_link']){
	$link = $fields['affiliate_link'];
 }

$foundkey = 99; 

// Make sure $coupons is an array before using array_column
if (is_array($coupons) && !empty($coupons)) {
    $key = array_search($useful[0]['domain'], array_column($coupons, 'website'));
    if ($key !== false) {
        $foundkey = $key;
    }
} else {
    // Handle the case where $coupons is not an array
    $foundkey = 99; // Keep the default value
}

?>

<div class="simpleitem">
	<div class="simpleitemtop">
		<div>
			<h<?=$fields['heading_type']?> data-heading-label="<?=$fields['product_tagline']?>" class="simpleitemtitle wp-block-heading"><span><?=$fields['product_tagline']?></span> <?=$name?></h<?=$fields['heading_type']?>>
		</div>
		<div>
		<?php
			if($pricetype == "dynamic"){
				echo '<a class="simpleitemdesktopcta afftrigger" href="'.afflink($link,$fields['product']).'" rel="sponsored external noopener">';
				if(isset($useful[0]['priceOld']) && $useful[0]['priceOld'] > 0){
					echo '<span>$'.number_format($useful[0]['priceOld'],2).'</span>';
				}
				echo '$'.number_format($useful[0]['price'],2).' at '.prettydomain($useful[0]['domain']);
				echo '</a>';
		
			} else {
				echo '<a class="simpleitemdesktopcta afftrigger" href="'.afflink($link,$fields['product']).'" rel="sponsored external noopener">';
				echo 'Check Price';
				echo '</a>';
			}
